import React from 'react';
import { useRouter } from 'next/router';

export default function Demo() {
    console.log("----------->", useRouter());
    const router = useRouter()
    if(useRouter().pathname == '/demo'){
        router.push('/posts');
    }
    return(
        <>
        <h1>Demo demo</h1>
        </>
    )
}

