// Next.js API route support: https://nextjs.org/docs/api-routes/introduction
var fs = require('fs');
var path = require('path');


export default (req, res) => {
  const resHtml = fetch('http://localhost:3000/posts',{method: 'get', headers: {'Content-Type':'text/html"'}});
  const resData = resHtml.text();
  console.log("resData========>", `${process.cwd()}/public/html/`);

  fs.writeFile(process.cwd()+"\public\html\static.html", resData, function(err) {
    if(err) {
        return console.log(err);
    }

    console.log("The file was saved!");
  }); 
  // res.statusCode = 200
  res.json({ name: 'John Doe' })
}
