    console.log("data=====================>", data);
  });
  console.log("API=====================>", api);
  res.statusCode = 200
  res.json({result: result, name: 'John Doe' })
}


async function api() {
  // console.log("res============>", res);
  const resHtml = await fetch('http://localhost:3000/posts',{method: 'get', headers: {'Content-Type':'text/html"'}});
  const resData = await resHtml.text();
  console.log("resData========>", `${process.cwd()}/public/html/`);

  fs.writeFile(process.cwd()+"/public/html/static.html", resData, function(err, result) {
    if(err) {
        return console.log(err);
    }
    console.log("result=====================>", result);
    return "The file was saved!";
