import React from 'react';
import { useRouter } from 'next/router';

export default function Demo() {
    console.log("----------->", useRouter());
    if(useRouter().pathname == '/demo'){
        Router.push('/posts');
    }
    return(
        <>
        <h1>Demo demo</h1>
        </>
    )
}

