import React from 'react';
import { useRouter } from 'next/router';
import { route } from 'next/dist/next-server/server/router';

export default function dyn({resData}){
    const router = useRouter()
    const { id } = router.query
    return(
        <>
            <p style={{textAlign: 'center'}}>Id: {resData.id}</p>
            <p style={{textAlign: 'center'}}>Title: {resData.title}</p> 
        </>
    )
}

export async function getServerSideProps({query}){
    const { id } = query
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`,{method: 'get', headers: {'Content-Type':'application/json'}})
    const resData = await res.json();

    return{
        props: {
            resData,
        }
    }
}
