// Next.js API route support: https://nextjs.org/docs/api-routes/introduction
var fs = require('fs');
var path = require('path');


export default (async(req, res) => {
  const resHtml = await fetch('http://localhost:3000/posts',{method: 'get', headers: {'Content-Type':'text/html"'}});
  const resData = await resHtml.text();
  console.log("resData========>", `${process.cwd()}/public/html/`);

  fs.writeFile(process.cwd()+"\public\html\static.html", resData, function(err) {
    if(err) {
        return console.log(err);
    }
    else{
      console.log(res, "The file was saved!");
      // res.statusCode = 200
      // res.json({ msg: 'The file was saved!' })
    }

    console.log("The file was saved!");
  }); 
})();
